// src/App.js
import React from 'react';
import { Grommet, Box, Header, Sidebar, Nav, Button, Icons, Footer, Text, Card, CardBody, CardHeader } from 'grommet';
import { Home } from 'grommet-icons';

import VideoEmbed from './VideoEmbed';


const customTheme = {
  global: {
    colors: {
      brand: '#01A982', // primary color
      background: '#f0f0f0',
      text: '#333333',
    },
    font: {
      family: 'Arial',
      size: '16px',
      height: '20px',
    },
  },
  button: {
    border: {
      radius: '4px',
    },
    color: 'brand',
  },
};

const Tile = ({ title, content }) => (
  <Card background="light-1" pad="medium" margin="small" elevation="small">
    <CardHeader>
      <Text weight="bold">{title}</Text>
    </CardHeader>
    <CardBody>
      <Text>{content}</Text>
    </CardBody>
  </Card>
);

function App() {
  return (
    <Grommet theme={customTheme} full>
      <Box fill>
      <Sidebar background="brand" round="small">

  <Nav gap="small">
    <Button icon={<Icons.Projects />} hoverIndicator />
    <Button icon={<Icons.Clock />} hoverIndicator />
  </Nav>
</Sidebar>
        <Header background="brand" pad="medium">
          <Home />
          <Text size="large" weight="bold">Player API Demo</Text>
        </Header>

          <Box wrap margin="large" width="large" height="medium" round="medium" elevation="medium" overflow="hidden" justify="center">
            <iframe
              width="100%"
              height="100%"
              src={`https://sketchfab.com/models/5d570c0e904a434f9a8dbedca39d89b8/embed?ui_infos=0&ui_watermark=0&ui_help=0&ui_vr=0&ui_settings=0&ui_inspector=0&ui_hint=2&ui_color=01a982&ui_theme=dark&autostart=1&camera=0`}
              title="DL380 Gen12"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />            
          </Box>
      </Box>
    </Grommet>
  );
}

export default App;
