// src/App.js
import React from 'react';
import { Grommet, Box, Header, Sidebar, Text, Card, CardBody, CardHeader } from 'grommet';
import { Home } from 'grommet-icons';
import SketchfabViewer from './SketchfabViewer';

const customTheme = {
  global: {
    colors: {
      brand: '#292d3a', // primary color
      background: '#f0f0f0',
      text: '#f0f0f0',
    },
    font: {
      family: 'Arial',
      size: '16px',
      height: '20px',
    },
  },
  button: {
    border: {
      radius: '4px',
    },
    color: 'brand',
  },
};

const Tile = ({ title, content }) => (
  <Card background="light-1" pad="medium" margin="small" elevation="small">
    <CardHeader>
      <Text weight="bold">{title}</Text>
    </CardHeader>
    <CardBody>
      <Text>{content}</Text>
    </CardBody>
  </Card>
);


function App() {
  return (
    <Grommet theme={customTheme} full>
      <Box fill>
        <Header background="brand" pad="medium">
          <Text size="large" weight="bold">Player API Demo</Text>
        </Header>

        <Box
          wrap
          margin="large"
          width="large"
          height="medium"
          round="medium"
          elevation="medium"
          overflow="hidden"
          alignSelf="center"
        >
          <div style={{ position: "relative", paddingBottom: "50.25%", height: 0 }}>
             <Box width="large" height="medium" round="small" elevation="medium" overflow="hidden">
               <iframe
                 width="100%"
                 height="100%"
                 src={`https://sketchfab.com/models/5d570c0e904a434f9a8dbedca39d89b8/embed?ui_infos=0&ui_watermark=0&ui_help=0&ui_vr=0&ui_settings=0&ui_inspector=0&ui_hint=2&ui_color=01a982&ui_theme=dark&autostart=1&camera=0`}
                 title="DL380 Gen12"
                 allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                 allowFullScreen
               />
             </Box>
          </div>
        </Box>
      </Box>
    </Grommet>
  );
}


export default App;
